mapper.insert:

	SELECT NVL(MAX(prj_board_id), 0) + 1
	FROM prj_board

INSERT INTO prj_board
				(
					prj_board_id
					, prj_board_title
					
					, prj_board_subject
					
					, prj_board_regdate
					, member_id
					
					, insp_yn
					
					, board_type
					, project_id
					
					, coordinate
					
				)
			VALUES
				(
					#{prjBoardId}
					, #{prjBoardTitle}
					
					, #{prjBoardSubject}
					
					, sysdate
					, #{memberId}
					
					, #{inspYn}
					
					, #{boardType}
					, #{projectId}
					
					, #{coordinate}
							
				)


	SELECT NVL(MAX(task_id), 0) + 1
	FROM prj_task

INSERT INTO prj_task
			(
				high_task_id
				
				, start_date
				
				
				, end_date
				
				
				, priority
				
				, state
				
				, processivity
				
				, prj_board_id
				, task_id
				, company_id
			)
			VALUES
			(
				#{highTaskId}
				
				, #{startDate}
				
				
				, #{endDate}
				
				
				, #{priority}
				
				, #{state}
				
				, #{processivity}
				
				, #{prjBoardId}
				, #{taskId}
				, #{companyId}
			)

INSERT INTO prj_vote
				(
					prj_board_id
					, end_date
					, anony_vote
					, compno_vote
					, result_yn
				)
			VALUES
				(
					#{prjBoardId}
					, #{endDate}
					, #{anonyVote}
					, #{compnoVote}
					, #{resultYn}	
				)


	SELECT NVL(MAX(list_id), 0) + 1
	FROM vote_list

	INSERT INTO vote_list
					(
