mapper.insert:

	SELECT NVL(MAX(list_id),0)+1
	FROM to_do_list

	INSERT INTO to_do_list(
						   list_id
						   , list_title
						   , list_regdate
						   , member_id
						   , apply_date
						   )
	VALUES (#{listId}
			, #{listTitle}
			, current_date
			, #{memberId}
			, #{applyDate}
			)


	SELECT NVL(MAX(item_id),0)+1
	FROM to_do_list_item

INSERT INTO to_do_list_item(
							content
							, success
							, item_id
							, list_id
							)
VALUES (
		#{content}
		, #{success}
		, #{itemId}
		, #{listId}
		)
