mapper.insert:

	SELECT NVL(MAX(noticeId), 0) + 1
	  FROM notice_board

INSERT INTO notice_board (
						  notice_id
	   					  ,notice_title
	   					  
	   					  ,notice_content
	   					  
	   					  ,member_id
	   					  ,board_regdate
	   					  ,board_upddate
	   					  ,coordinate
	   					  ,board_hit
	   					  ,board_type
	   					  ,company_id
	   					  )
	   					  VALUES
	   					  (
	   					   #{noticeId}
	   					  ,#{noticeTitle}
	   				  	  ,#{noticeContent}
	   				 	  ,#{memberId}
	   					  ,#{boardRegdate}
	   					  ,#{boardUpddate}
	   					  ,#{coordinate}
	   					  ,#{boardHit}
	   					  ,#{boardType}
	   					  ,{companyId}
	   					  )
