mapper.update:
UPDATE	prj_particir
SET		particir_accp = 'A1'
WHERE	member_id = #{memberId}
	AND	project_id = #{projectId}

mapper.select:
SELECT p.project_id, p.project_name, find_subcode(p.project_access) AS project_access, find_subcode(p.manager_accp) AS manager_accp, find_subcode(p.write_access) AS write_access, find_subcode(p.update_access) AS update_access, find_subcode(p.board_access) AS board_access, find_subcode(p.file_access) AS file_access, find_subcode(p.comment_access) AS comment_access, p.dept_id, find_subcode(p.project_cls) AS project_cls, (SELECT COUNT(*) from prj_particir pp WHERE p.project_id=pp.project_id AND particir_accp='A1') AS prj_particir_num FROM project p WHERE dept_id IN (SELECT dept_id FROM department WHERE company_id= #{companyId}) AND p.project_cls= nvl(#{projectCls},'A2')
AND dept_id=#{deptId}
ORDER BY p.project_id

mapper.update:
UPDATE prj_particir 
	
		project_markup=#{projectMarkup}
	 
   WHERE project_id=#{projectId} 
   AND member_id=#{memberId}

mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT f.board_type, f.file_name, f.file_path, f.file_type, f.file_regdate, f.file_size, f.board_id, f.real_file_path, f.file_id, f.project_id, f.member_id, m.member_name FROM file_data f JOIN ( SELECT member_name, member_id FROM member ) m ON m.member_id = f.member_id WHERE f.project_id = #{vo.projectId}
AND f.file_name LIKE '%' || #{searchVO.searchkeyword} || '%'
AND m.member_name LIKE '%' || #{searchVO.searchkeyword} || '%'
ORDER BY file_id DESC ) a ) WHERE rn BETWEEN #{pagingVO.start} AND #{pagingVO.end}

SELECT manager 
FROM prj_particir 
WHERE member_id=#{memberId} 
		AND project_id=#{projectId}
		AND manager='A1'

SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT f.board_type, f.file_name, f.file_path, f.file_type, f.file_regdate, f.file_size, f.board_id, f.real_file_path, f.file_id, f.project_id, f.member_id, m.member_name FROM file_data f
LEFT JOIN ( SELECT member_id, member_name FROM member ) m ON m.member_id = f.member_id WHERE f.project_id = #{vo.projectId} AND f.member_id = #{vo.memberId}
AND f.file_name LIKE '%' || #{searchVO.searchkeyword} || '%'
AND m.member_name LIKE '%' || #{searchVO.searchkeyword} || '%'
ORDER BY file_id DESC ) a ) WHERE rn BETWEEN #{pagingVO.start} AND #{pagingVO.end}

SELECT COUNT(f.member_id) AS member_count, MAX(f.member_id) AS max_member_id, MAX(m.member_name) AS max_member_name FROM file_data f JOIN ( SELECT member_id, member_name FROM member ) m ON f.member_id = m.member_id WHERE project_id = #{vo.projectId}
AND file_name like '%' || #{searchVO.searchkeyword } || '%'
AND member_name like '%' || #{searchVO.searchkeyword } || '%'

SELECT COUNT(f.member_id) AS member_count, MAX(f.member_id) AS max_member_id, MAX(m.member_name) AS max_member_name FROM file_data f JOIN ( SELECT member_id, member_name FROM member ) m ON f.member_id = m.member_id WHERE project_id = #{vo.projectId} AND m.member_id = #{vo.memberId}
AND file_name like '%' || #{searchVO.searchkeyword } || '%'
AND member_name like '%' || #{searchVO.searchkeyword } || '%'

mapper.insert:

	SELECT NVL(MAX(file_id), 0) + 1
	FROM file_data

INSERT INTO file_data(           
	file_name      
	,file_path     
	,file_type     
	,file_regdate     
	,file_size       
	,real_file_path 
	,file_id        
	,project_id         
	,member_id 
	,board_type
	,board_id    
)values(
	#{fileName}
	,#{filePath}
	,#{fileType}
	,#{fileRegdate}
	,TO_CHAR(#{fileSize})
	,#{realFilePath}
	,#{fileId}
	,#{projectId}
	,#{memberId}
	,#{boardType}
	,#{boardId}
)
