mapper.select:
SELECT p.prj_board_id, p.prj_board_title, p.prj_board_regdate, p.member_id, find_subcode(p.board_type) board_type, p.project_id, m.member_name, m.company_id, pj.project_name FROM prj_board p LEFT JOIN( SELECT member_name, member_id, company_id FROM member )m ON m.member_id=p.member_id AND m.company_id=#{companyId}
LEFT JOIN( SELECT project_id, project_name FROM project WHERE project_access='A1' )pj ON pj.project_id=p.project_id
WHERE prj_board_title LIKE '%'||#{SearchKeyword}||'%'
